import React, { useState } from "react";
import './Prueba1.css';

async function getUserByID(id) {
 return fetch('http://127.0.0.1:8090/user/' + id, {
   method: 'GET',
   headers: {
     'Content-Type': 'application/json'
     //Access-Control-Allow-Origin
   }
 })
   .then(data => data.json())
}


function Prueba1() {

  const [userData, setUserData] = useState({});
  const [isUser, setIsUSer] = useState(false);

  const handleSubmit = async event => {
    //Prevent page reload
    event.preventDefault();

    // Obtenemos Textos
    var {uid}  = document.forms[0];


    // Find user login info
    const user = await getUserByID(uid.value);

    alert(user);

    setUserData(user);
    setIsUSer(true);

  };


  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>User ID </label>
          <input type="text" name="uid" required />
        </div>
        <div className="button-container">
          <input type="submit" value="Send Request" />
        </div>
      </form>
    </div>
  );

  return (
    <div className="app">
      <div className="login-form">
        <div className="title">Pruebas Arq. Soft. {userData.name}</div>
          {isUser? <div>Usuario: {userData.name} </div> : renderForm}
      </div>
    </div>
  );
}

export default Prueba1;
